from typing import List

"""
Constraints:
2 <= nums.length <= 1000
0 <= nums[i] <= 500
"""

class Solution:
    def longestArithSeqLength(self, nums: List[int]) -> int:
        res = 2
        for i in range(len(nums)):
            steps = dict()
            for j in range(i,len(nums)):
                if i == j:
                    continue

                for step in steps.keys():
                    if nums[j] == steps[step][0] + step:  # found next steps
                        steps[step][1] = steps[step][1] + 1
                        steps[step][0] = nums[j]

                if not steps.get(nums[j] - nums[i]):
                    steps[nums[j] - nums[i]] = [nums[j], 2]

            for step in steps.values():
                if step[1] > res:
                    res = step[1]

        return res

